.right {
  width: 900px;
  height: 400px;
}
.stl,
.welcome {
  margin-left: 600px;
}
.left {
  font-size: 12px; /* Overall text size kam hoga */
}

.welcome {
  font-size: 18px; /* Heading ke liye */
}

.stl {
  font-size: 12px; /* Description text ke liye */
}
.welcome {
  margin-top: 2px;
}

.quiz-container {
  margin-left: 200px;
  margin-top: 12px;
}
.about {
  margin-left: 12px;
  margin-top: 12px;
}
.progress {
  margin-right: 142px;
}
.resources-container {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  justify-content: center;
  max-width: 1200px;
  margin: 20px auto;
  padding: 10px;
}

.card {
  width: 100%;
  max-width: 280px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
  border-radius: 12px;
  overflow: hidden;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  background: white;
}

.card:hover {
  transform: scale(1.05);
  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.25);
}

.card-img-top {
  width: 100%;
  height: 180px;
  object-fit: cover;
  border-bottom: 2px solid #ddd;
}

.card-body {
  padding: 15px;
}

.card-title {
  font-size: 1.2rem;
  font-weight: bold;
  color: #333;
}

.card-text {
  font-size: 0.9rem;
  color: #555;
  margin: 10px 0;
}

.btn-redeem {
  background: #ff9800;
  color: white;
  padding: 8px 12px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-weight: bold;
  transition: background 0.3s;
}

.btn-redeem:hover {
  background: #e68900;
}
